@layer base {
	/* shadcn tokens */
  :root {
    /* Base colors derived from Zapier design tokens */
    --background: var(--zds-background-stronger);
    --foreground: var(--zds-text-default);
    --card: var(--zds-background-weaker);
    --card-foreground: var(--zds-text-stronger);
    --popover: var(--zds-background-weaker);
    --popover-foreground: var(--zds-text-stronger);

		/* Brand colors */
    --brand: var(--zds-ui-brand);
    --brand-foreground: var(--zds-ui-brand-weakest);

    /* Primary colors */
    --primary: var(--zds-ui-primary-strongest);
    --primary-foreground: var(--zds-ui-primary-weakest);

    /* Secondary colors */
    --secondary: var(--zds-ui-primary-weaker);
    --secondary-foreground: var(--zds-ui-primary-stronger);

    /* Muted colors */
    --muted: var(--zds-background-strongest);
    --muted-foreground: var(--zds-text-weakest);

    /* Accent colors */
    --accent: var(--zds-ui-primary-weakest);
    --accent-foreground: var(--zds-text-stronger);

    /* Destructive colors */
    --destructive: var(--zds-status-error);
    --destructive-foreground: var(--zds-status-error-weakest);

    /* Border and input colors */
    --border: var(--zds-stroke-weaker);
    --input: var(--zds-stroke-default);
    --ring: var(--zds-ui-primary);

    /* Chart colors from Zapier's palette */
    --chart-1: var(--zds-brand-orange);
    --chart-2: var(--zds-prime-teal);
    --chart-3: var(--zds-prime-blue);
    --chart-4: var(--zds-brand-peach);
    --chart-5: var(--zds-prime-purple);

    /* Additional useful tokens from Zapier */
    --success: var(--zds-status-success);
    --success-foreground: var(--zds-status-success-weakest);
    --warning: var(--zds-status-warning);
    --warning-foreground: var(--zds-status-warning-weakest);
    --info: var(--zds-prime-blue);
    --info-foreground: var(--zds-color-blue-1);

    /* Border radius */
    --radius: var(--zds-radius-medium);

    /* Sidebar specific tokens */
    --sidebar-background: var(--zds-background-default);
    --sidebar-foreground: var(--zds-text-default);
    --sidebar-primary: var(--zds-ui-primary);
    --sidebar-primary-foreground: var(--zds-background-weaker);
    --sidebar-accent: var(--zds-ui-brand-weakest);
    --sidebar-accent-foreground: var(--zds-ui-brand);
    --sidebar-border: var(--zds-stroke-weaker);
    --sidebar-ring: var(--zds-ui-primary);
  }
}

